select shipper1.companyname, round((select count(*) from 'order' order1, shipper shipper2 where ShippedDate > RequiredDate and shipper2.id == order1.shipvia and shipper2.companyname == shipper1.companyname)*100.00/(select count(*) from 'order' order1, shipper shipper2 where shipper2.id == order1.shipvia and shipper2.companyname == shipper1.companyname),2) as percentage from shipper shipper1 order by percentage;
